declare module 'vue-fragment';

declare module 'vue-code-highlight';

declare module 'vue-touch';

declare module '*.svg' {
	const content: string;
	export default content;
}

declare module '*.png' {
	const content: string;
	export default content;
}

declare module '*.jpg' {
	const content: string;
	export default content;
}

declare module 'raw-loader!*' {
	const content: string;
	export default content;
}
